package com.biz.hello;

public class VarAdd_01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1, num2 = 0;
		num1 = 5;
		num2 = 25;

		System.out.println(num1 + " + " + num2 + " = " + (num1 + num2));
		System.out.println(num1 + " - " + num2 + " = " + (num1 - num2));
		System.out.println(num1 + " * " + num2 + " = " + (num1 * num2));
		System.out.println(num2 + " / " + num1 + " = " + (num2 / num1));
	}
}